import './Request.css'
import './RadioButtons.css'
import { useState } from 'react'
import { Button } from '../Button/Button.js'

function sendData() {
    console.log()
    // fetch('https://localhost:8080/api/Request', {
    //     method: 'POST',
    //     headers: {
    //         'Accept': 'application/json',
    //         'Content-Type': 'application/json',
    //     },
    //     body: JSON.stringify({
    //         firstParam: e.target.,
    //         secondParam: 'yourOtherValue',
    //     })
    // })

}


export function Request() {
    const [devType, setDevType] = useState("")
    const [text, setText] = useState("")

    const handleChange = e => {
        setDevType(e.target.value)
        setText(e.target.value)
    }

    console.log(devType)
    console.log(text)
    return (
        <div className="request">
            <div className="title-request mb-3">
                <h1 className="title-request-text">Оставьте заявку</h1>
            </div>
            <form id="form" className='mb-3' method='post'>
                <div className="radio-buttons">
                    <fieldset>
                        <div className="form-check-inline">
                            <input className="form-check-input" type="radio" name="DevType" value="1" id="radioWebSites" onChange={handleChange} />
                            <label className="form-check-label" htmlFor="radioWebSites" value="1">Web sites</label>
                        </div>
                        <div className="form-check-inline">
                            <input className="form-check-input" type="radio" name="DevType" value="2" id="AI" onChange={handleChange} />
                            <label className="form-check-label" htmlFor="AI" >Android/iOS</label>
                        </div>
                        <div className="form-check-inline">
                            <input className="form-check-input" type="radio" name="DevType" value="3" id="DevOps" onChange={handleChange} />
                            <label className="form-check-label" htmlFor="DevOps">DevOps</label>
                        </div>
                    </fieldset>
                </div>
                <div className="back-block-request">
                    <div className="text-field-request">
                        <input type='text' className='text-field-request-text' onChange={handleChange}></input>
                    </div>
                    <Button />
                </div>
            </form>
        </div>
    )
}
