import React from 'react';
import { Link } from 'react-router-dom';

const Unauthorized = () => {
    const styles = {
        body: {
            fontFamily: 'Arial, sans-serif',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            margin: 0,
            backgroundColor: '#f0f0f0',
            color: '#333',
        },
        container: {
            textAlign: 'center',
            padding: '20px',
            backgroundColor: 'white',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        },
        heading: {
            fontSize: '24px',
            marginBottom: '20px',
        },
        text: {
            fontSize: '16px',
            marginBottom: '20px',
        },
        link: {
            display: 'inline-block',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: 'white',
            textDecoration: 'none',
            borderRadius: '4px',
        },
        linkHover: {
            backgroundColor: '#0056b3',
        }
    };

    return (
        <div style={styles.body}>
            <div style={styles.container}>
                <h1 style={styles.heading}>Доступ запрещён</h1>
                <p style={styles.text}>
                    Вы не авторизованы для просмотра этой страницы. Пожалуйста, войдите в систему.
                </p>
                <Link to="/login" style={styles.link}>Войти</Link>
            </div>
        </div>
    );
};

export default Unauthorized;
