import { useNavigate } from 'react-router-dom'
import { useEffect, useState } from 'react'
import './Services.css'

export function Services() {
    const [blocks, setBlocks] = useState([
        { id: 1, title: 'Web Ð Ð°Ð·Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ°', description: 'Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ Ð¸ Ñ€Ð°Ð·Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° ÑÐ¾Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ñ‹Ñ… ÑÐ°Ð¹Ñ‚Ð¾Ð² Ð¸ Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ð¹', photo: '' },
        { id: 2, title: 'Android/iOS', description: 'Ð Ð°Ð·Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ð¹ Ð´Ð»Ñ iOS Ð¸ Android', photo: '' },
        { id: 3, title: 'DevOps Ð¸ Ð¾Ð±Ð»Ð°ÐºÐ¾', description: 'ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ° Ð¸Ð½Ñ„Ñ€Ð°ÑÑ‚Ñ€ÑƒÐºÑ‚ÑƒÑ€Ñ‹ Ð¸ Ð°Ð²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ð·Ð°Ñ†Ð¸Ñ Ð¿Ñ€Ð¾Ñ†ÐµÑÑÐ¾Ð²', photo: '' }
    ]);

    useEffect(() => {
        getImagesNames()
            .then(result => {
                const updated = [...blocks];
                updated[1].photo = result["ImagesData"][0]['ImageURL'];
                updated[2].photo = result["ImagesData"][1]['ImageURL'];
                updated[0].photo = result["ImagesData"][2]['ImageURL'];
                setBlocks(updated); // ðŸ’¡ Ð¾Ð±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ñ‡ÐµÑ€ÐµÐ· setState
            })
            .catch(error => console.log('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ð¸ Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ð¹: ', error));
        console.log(blocks)
    }, [])



    const nav = useNavigate()

    const handler = (id, block) => {
        nav(`/about/${id}`, { state: block })
    }

    return (
        <div className='blocks'>
            <div className='blocks-group'>
                {blocks.map(block => (
                    <div key={block.id} onClick={() => handler(block.id, block)} className='back-blocks'>
                        <div className="front-blocks">
                            <div className='blocks-text'>
                                <div className="blocks-title">{block.title}</div>
                                <div className="blocks-description">{block.description}</div>
                                <div className="blocks-image">
                                    <img className="blocks-image-source" src={block.photo} alt={block.title} />
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )
}

async function getImagesNames() {
    var res = await fetch("https://localhost:8080/api/minio/images")
    var ans = await res.json()
    return ans
}
